mapper.update:
UPDATE 
JOIN agenda ON agenda.agenda_id = .agenda_id
AND agenda.next_time = .summary_time
AND agenda.agenda_id = #{agendaId}
SET summary_time = #{summaryTime}

mapper.delete:
DELETE  FROM

JOIN agenda ON agenda.agenda_id = .agenda_id
AND agenda.agenda_id IN #{agendaId}
AND agenda.next_time = .summary_time
AND agenda.owner_id = #{userId}
AND agenda.open = 0
